import { Button, Input, Modal } from "@mui/material";
import axios from "axios";
import React, { useEffect, useRef, useState } from "react";
import SeatsData from "../helpers/seats.json";
import { getObjectClassNames } from "design/utils";

type DeckProps = {
  seats: Array<Seat>;
  onClick: any;
  deckType: string;
};

type SeatProps = {
  seat: Seat;
  onClick: any;
};

type Seat = {
  type: string;
  number: string;
  status: string;
};

type ReservationModal = {
  seat: Seat;
  onSubmit: any;
  onCancel: any;
};

type UserData = {
  first_name: string;
  last_name: string;
  email: string;
};

type BusDetails = {
  number: string;
};

const deckClasses = getObjectClassNames({
  container: {
    diplay: 'flex',
    flexDirection: 'row'
  },
  busHandle: {},
});

const seatClasses = getObjectClassNames({
  container: {
    height: 40,
    width: 40,
    display: "flex",
    justifyContent: "space-around",
  },
  head: {
    display: "flex",
    width: 15,
    height: 15,
    marginLeft: 5,
  },
});
const ReservationModal = (props: ReservationModal) => {
  const [userData, setUserData] = useState<UserData>({
    first_name: "",
    last_name: "",
    email: "",
  });

  const handleChange = (key: string, value: string) => {
    const temp = userData;
    temp[key] = value;
    setUserData(temp);
  };

  return (
    <React.Fragment>
      {["First Name", "Last Name", "Email"].map((t) => {
        return (
          <Input
            name={t}
            onChange={(e) => {
              handleChange(t, e.target.value);
            }}
          />
        );
      })}

      <div>
        <Button onClick={() => props.onSubmit(userData)}>Book</Button>
        <Button onClick={props.onCancel}>Cancel</Button>
      </div>
    </React.Fragment>
  );
};

const Seat = (props: SeatProps) => {
  // const background = props.seat.status == "confirmed" ? "green" : "gray";
  const background = 'green'
  return (
    <div className={seatClasses.container} style={{ background: background }}>
      <text>{props.seat?.number}</text>
      <div className={seatClasses.head}></div>
    </div>
  );
};
const Deck = (props: DeckProps) => {
  return (
    <div className={deckClasses.container} style={{}}>
      <div>
        {props.deckType === "lower" && (
          <div className={deckClasses.busHandle} />
        )}
      </div>
      <div>
        <div>
          {props.seats.map((seat, i) => {
            if (i < 5) return <Seat seat={seat} onClick={props.onClick} />;
          })}
        </div>
        <div>
          {props.seats.map((seat, i) => {
            if (i >= 5 && i < 10)
              return <Seat seat={seat} onClick={props.onClick} />;
          })}
        </div>
        <div>
          {props.seats.map((seat, i) => {
            if (i >= 10 && i < 15)
              return <Seat seat={seat} onClick={props.onClick} />;
          })}
        </div>
      </div>
      <div>
        <Seat
          seat={props.seats[props.seats.length - 1]}
          onClick={props.onClick}
        />
      </div>
    </div>
  );
};
const SeatLayout = () => {
  const [busSeats, setBusSeats] = useState<Array<Seat>>([]);
  const [busDetails, setBusDeatils] = useState<BusDetails>({ number: "" });
  const [showReservationModal, setShowReservationModal] =
    useState<boolean>(false);
  const seatRef = useRef<Seat>({
    type: "",
    number: "",
    status: "",
  });
  useEffect(() => {
    (async () => {
      //   const response = await axios.get("bus seats");
      const response = SeatsData;
      setBusSeats(response.data.seats);
      setBusDeatils(response.data.bus_details);
    })();
  });

  const onCancel = () => {
    setShowReservationModal(false);
  };
  const onFailure = () => {};
  const onSuccess = () => {
    setShowReservationModal(false);
  };

  const onSubmit = async (data: UserData) => {
    try {
      const response = await axios.post("make reservation", {
        ...data,
        bus_number: busDetails.number,
        seat_number: seatRef.current.number,
      });
      response.status == 200 ? onSuccess() : onFailure();
    } catch (e) {
      onFailure();
      console.log("Error occurred while making a resevation", e);
    }
  };
  const onSeatSelect = (seat: Seat) => {
    seatRef.current = seat;
    setShowReservationModal(true);
  };
  return (
    <React.Fragment>
      <span>Click on avaliable seats to make a reservation.</span>
      <span>Lower Deck</span>
      <Deck
        deckType={"lower"}
        seats={busSeats.slice(0, busSeats.length / 2)}
        onClick={(e: any) => onSeatSelect(e.target.value)}
      />
      <span>Upper Deck</span>
      <Deck
        deckType={"upper"}
        seats={busSeats.splice(busSeats.length / 2, busSeats.length)}
        onClick={(e: any) => onSeatSelect(e.target.value)}
      />
      <Modal open={showReservationModal}>
        <ReservationModal
          seat={seatRef.current}
          onSubmit={onSubmit}
          onCancel={onCancel}
        />
      </Modal>
    </React.Fragment>
  );
};

export default SeatLayout;
