import { Modal } from "@mui/material";
import React, { useEffect, useRef, useState } from "react";
import SeatsData from "../helpers/seats.json";
import { getObjectClassNames } from "design/utils";
import { Bus, BusMenu } from "./common/BusMenu";
import { BookingModal, UserData } from "./common/BookingModal";
import Request from "helpers/request";

type DeckProps = {
  seats: Array<Seat>;
  onClick: any;
  deckType: string;
};

type SeatProps = {
  seat: Seat;
  onClick: any;
  className?: any;
  headClass?: any;
};

type Seat = {
  id: string;
  type: string;
  number: string;
  status: string;
};

type ReservationModal = {
  seat: Seat;
  onSubmit: any;
  onCancel: any;
};

const deckClasses = getObjectClassNames({
  container: {
    display: "flex",
    flexDirection: "row",
    width: "50%",
    height: 400,
    background: "floralwhite",
    borderRadius: 3,
    boxShadow: "3px 3px 3px gray",
  },
  busHandle: {
    display: "flex",
    width: "100%",
    boxShadow: "3px 0px 1px grey",
    borderRight: "1px solid black",
    height: "100%",
  },
});

const classes = getObjectClassNames({
  container: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    background: "ghostwhite",
    padding: 10,
  },
  reservationText: {
    background: "red",
    margin: "10px 0px 5px 0px",
    padding: 10,
    borderRadius: 3,
  },
  headerText: {
    padding: 10,
    margin: 5,
    fontSize: 24,
    fontWeight: 600,
  },
  busContainer: {
    width: "50%",
  },
});

const seatClasses = getObjectClassNames({
  container: {
    height: 50,
    width: 100,
    display: "flex",
    justifyContent: "space-around",
    borderRadius: 3,
    flexDirection: "row",
  },
  head: {
    width: 15,
    height: 30,
  },
  backSeat: {
    height: "100px !important",
    width: "50px !important",
    borderRadius: 3,
    flexDirection: "column",
  },
  backSeatHead: {
    width: 30,
    height: 15,
  },
});

const Seat = (props: SeatProps) => {
  const background =
    props.seat?.status == "confirmed" ? "darkseagreen" : "gray";
  return (
    <div
      className={props.className}
      style={{
        background: background,
        margin: 5,
        padding: 5,
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        cursor: "pointer",
      }}
      onClick={props.onClick}
    >
      <text>{props.seat?.number}</text>
      <div
        className={props.headClass}
        style={{
          display: "flex",
          marginLeft: 5,
          border: "1px solid black",
          borderRadius: 3,
          alignItems: "center",
          background: "darkslategrey",
        }}
      ></div>
    </div>
  );
};
const Deck = (props: DeckProps) => {
  return (
    <div className={deckClasses.container} style={{}}>
      <div style={{ width: "10%" }}>
        {props.deckType === "lower" && (
          <div className={deckClasses.busHandle} />
        )}
      </div>
      <div
        style={{
          width: "80%",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <div style={{ display: "flex", flexDirection: "row", marginTop: 10 }}>
          {props.seats.map((seat, i) => {
            if (i < 5)
              return (
                <Seat
                  seat={seat}
                  onClick={props.onClick}
                  className={seatClasses.container}
                  headClass={seatClasses.head}
                />
              );
          })}
        </div>
        <div style={{ display: "flex", flexDirection: "row" }}>
          {props.seats.map((seat, i) => {
            if (i >= 5 && i < 10)
              return (
                <Seat
                  seat={seat}
                  onClick={props.onClick}
                  className={seatClasses.container}
                  headClass={seatClasses.head}
                />
              );
          })}
        </div>
        <div style={{ display: "flex", flexDirection: "row", marginTop: 100 }}>
          {props.seats.map((seat, i) => {
            if (i >= 10 && i < 15)
              return (
                <Seat
                  seat={seat}
                  onClick={props.onClick}
                  className={seatClasses.container}
                  headClass={seatClasses.head}
                />
              );
          })}
        </div>
      </div>
      <div style={{ width: "10%", display: "flex", alignItems: "center" }}>
        <Seat
          seat={props.seats[props.seats.length - 1]}
          onClick={props.onClick}
          className={seatClasses.backSeat}
          headClass={seatClasses.backSeatHead}
        />
      </div>
    </div>
  );
};
const SeatLayout = () => {
  const [busSeats, setBusSeats] = useState<Array<Seat>>([]);
  const [busDetails, setBusDeatils] = useState<Bus>();
  const [showReservationModal, setShowReservationModal] =
    useState<boolean>(false);
  const seatRef = useRef<Seat>({
    type: "",
    number: "",
    status: "",
    id: "",
  });
  useEffect(() => {
    (async () => {
      if (busDetails?.id) {
        const response = await Request.get(`/seats?bus_id=${busDetails?.id}`);
        console.log(busSeats)
        setBusSeats(response.data);
      }
    })();
  }, [busDetails?.id]);

  const onCancel = () => {
    setShowReservationModal(false);
  };
  const onFailure = () => {
    setShowReservationModal(false);
  };
  const onSuccess = () => {
    setShowReservationModal(false);
  };

  const onSubmit = async (data: UserData) => {
    try {
      const response = await Request.post("/reservations", {
        passenger_details: data,
        bus_id: busDetails.id,
        seat_id: seatRef.current.id,
      });
      response.status == 200 ? onSuccess() : onFailure();
    } catch (e) {
      onFailure();
      console.log("Error occurred while making a resevation", e);
    }
  };
  const onSeatSelect = (seat: Seat) => {
    seatRef.current = seat;
    setShowReservationModal(true);
  };

  const onBusSelect = (value: Bus) => {
    setBusDeatils(value);
  };
  return (
    <div className={classes.container}>
      <BusMenu
        onBusSelect={onBusSelect}
        containerClass={classes.busContainer}
      />
      <span className={classes.reservationText}>
        Click on avaliable seats to make a reservation.
      </span>
      <span className={classes.headerText}>Lower Deck</span>
      <Deck
        deckType={"lower"}
        seats={busSeats.slice(0, busSeats.length / 2)}
        onClick={(e: any) => onSeatSelect(e.target.value)}
      />
      <span className={classes.headerText}>Upper Deck</span>
      <Deck
        deckType={"upper"}
        seats={busSeats.slice(busSeats.length / 2, busSeats.length)}
        onClick={(e: any) => onSeatSelect(e.target.value)}
      />
      <Modal open={showReservationModal}>
        <BookingModal onSubmit={onSubmit} onCancel={onCancel} />
      </Modal>
    </div>
  );
};

export default SeatLayout;
