import React, { useEffect, useRef, useState } from "react";
import {
  Button,
  Input,
  Modal,
  Table,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from "@mui/material";
import axios from "axios";

type Reservation = {
  first_name: string;
  last_name: string;
  email: string;
  seat_number: string;
  booking_date: string;
};

type UpdatedValue = {
  first_name?: string;
  last_name?: string;
  email?: string;
};

const ReservationDashboard = () => {
  const [reservations, setReservations] = useState<Array<Reservation>>([]);
  const [updatedValues, setUpdatedValues] = useState<UpdatedValue>({});
  const [showEditView, setShowEditView] = useState<boolean>(false);
  const currIndexRef = useRef<number>(0);

  useEffect(() => {
    (async () => {
      const response = await axios.get("reservations");
      setReservations(response.data);
    })();
  });

  const onUpdate = (key: string, value: string) => {
    const temp = updatedValues;
    temp[key] = value;
    setUpdatedValues(temp);
  };

  const onEdit = (index: number) => {
    currIndexRef.current = index;
    setShowEditView(true);
  };

  const onSaveChanges = () => {
    const temp = reservations;
    temp[currIndexRef.current] = {
      ...temp[currIndexRef.current],
      ...updatedValues,
    };
    setReservations(temp);
    setShowEditView(false);
  };

  const onCancel = () => {
    setShowEditView(false);
  };

  const editView = () => {
    const data = reservations[currIndexRef.current];
    return (
      <div style={{ display: "flex" }}>
        <Input
          defaultValue={data.first_name}
          name="First Name"
          onChange={(e) => onUpdate("first_name", e.target.value)}
        />
        <Input
          defaultValue={data.last_name}
          name="Last Name"
          onChange={(e) => onUpdate("last_name", e.target.value)}
        />
        <Input
          defaultValue={data.email}
          name="Email"
          onChange={(e) => onUpdate("email", e.target.value)}
        />
        <div style={{ display: "flex" }}>
          <Button onClick={onSaveChanges}>Save</Button>
          <Button onClick={onCancel}>Cancel</Button>
        </div>
      </div>
    );
  };

  const onDelete = (index) => {
    const temp = reservations;
    temp.splice(index, 1);
    setReservations(temp);
  };

  return (
    <TableContainer>
      <Table>
        <TableHead>
          <TableRow>
            {["Serial No.", "Name", "Email", "Seat No.", "Date of booking"].map(
              (e) => {
                return <TableCell>{e}</TableCell>;
              }
            )}
          </TableRow>
          {reservations.map((e, i) => {
            return (
              <TableRow>
                <TableCell>
                  {e.first_name} {e.last_name}
                </TableCell>
                <TableCell>{e.email}</TableCell>
                <TableCell>{e.seat_number}</TableCell>
                <TableCell>{e.date_of_booking}</TableCell>
                <TableCell>
                  <Button onClick={() => onEdit(i)}>Edit</Button>
                </TableCell>
                <TableCell>
                  <Button onClick={() => onDelete(i)}>Delete</Button>
                </TableCell>
              </TableRow>
            );
          })}
          <Modal open={showEditView}>{editView()}</Modal>
        </TableHead>
      </Table>
    </TableContainer>
  );
};

export default ReservationDashboard;
