import React, { useEffect, useRef, useState } from "react";
import {
  Button,
  Input,
  Modal,
  Table,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from "@mui/material";
import axios from "axios";
import DashboardData from "../helpers/dashboard.json";
import { getObjectClassNames } from "../design/utils";

const classes = getObjectClassNames({
  tableCellHeader: {},
  tableContatiner: {
    width: "90%",
    alignSelf: 'center'
  },
  tableHead: {},
  tableCell: {},
});
type Reservation = {
  first_name: string;
  last_name: string;
  email: string;
  seat_number: string;
  booking_date: string;
};

type UpdatedValue = {
  first_name?: string;
  last_name?: string;
  email?: string;
};

type EditView = {
  onSaveChanges: any;
  onCancel: any;
  onUpdate: any;
  index: number;
  reservations: Array<Reservation>;
};

const EditView = (props: EditView) => {
  const data = props.reservations[props.index];
  return (
    <div style={{ display: "flex" }}>
      <Input
        defaultValue={data.first_name}
        name="First Name"
        onChange={(e) => props.onUpdate("first_name", e.target.value)}
      />
      <Input
        defaultValue={data.last_name}
        name="Last Name"
        onChange={(e) => props.onUpdate("last_name", e.target.value)}
      />
      <Input
        defaultValue={data.email}
        name="Email"
        onChange={(e) => props.onUpdate("email", e.target.value)}
      />
      <div style={{ display: "flex" }}>
        <Button onClick={props.onSaveChanges}>Save</Button>
        <Button onClick={props.onCancel}>Cancel</Button>
      </div>
    </div>
  );
};

const ReservationDashboard = () => {
  const [reservations, setReservations] = useState<Array<Reservation>>([]);
  const [updatedValues, setUpdatedValues] = useState<UpdatedValue>({});
  const [showEditView, setShowEditView] = useState<boolean>(false);
  const currIndexRef = useRef<number>(0);

  useEffect(() => {
    // (async () => {
    //   const response = await axios.get("reservations");
    //   setReservations(response.data);
    // })();
    setReservations(DashboardData);
    debugger;
  });

  // useEffect(() => {
  //   console.log('running')
  // }, [reservations.length]);

  const onUpdate = (key: string, value: string) => {
    const temp = updatedValues;
    temp[key] = value;
    setUpdatedValues(temp);
  };

  const onEdit = (index: number) => {
    currIndexRef.current = index;
    setShowEditView(true);
  };

  const onSaveChanges = () => {
    const temp = reservations;
    temp[currIndexRef.current] = {
      ...temp[currIndexRef.current],
      ...updatedValues,
    };
    setReservations(temp);
    setShowEditView(false);
  };

  const onCancel = () => {
    setShowEditView(false);
  };

  const editView = () => {};

  const onDelete = (index: number) => {
    const temp = [...reservations];
    temp.splice(index, 1);
    console.log(temp);
    setReservations(temp);
  };

  return (
    <TableContainer className={classes.tableContatiner}>
      <Table>
        <TableHead>
          <TableRow>
            {[
              "Serial No.",
              "Name",
              "Email",
              "Seat No.",
              "Date of booking",
              "",
              "",
            ].map((e) => {
              return <TableCell  className={classes.tableCellHeader}>{e}</TableCell>;
            })}
          </TableRow>
          {reservations.map((e, i) => {
            return (
              <TableRow>
                <TableCell>{i + 1}</TableCell>
                <TableCell>
                  {e.first_name} {e.last_name}
                </TableCell>
                <TableCell>{e.email}</TableCell>
                <TableCell>{e.seat_number}</TableCell>
                <TableCell>{e.booking_date}</TableCell>
                <TableCell>
                  <Button onClick={(e) => onEdit(i)}>Edit</Button>
                </TableCell>
                <TableCell>
                  <Button onClick={(e) => onDelete(i)}>Delete</Button>
                </TableCell>
              </TableRow>
            );
          })}
          <Modal open={showEditView}>
            <EditView
              onUpdate={onUpdate}
              onCancel={onCancel}
              onSaveChanges={onSaveChanges}
              reservations={reservations}
              index={currIndexRef.current}
            />
          </Modal>
        </TableHead>
      </Table>
    </TableContainer>
  );
};

export default ReservationDashboard;
