import { Button } from "@mui/material";
import { getObjectClassNames } from "design/utils";
import React, { useState } from "react";

const modalClasses = getObjectClassNames({
  container: {
    flexDirection: "column",
    background: "aliceblue",
    flexWrap: "wrap",
    width: "40%",
    margin: 50,
    alignContent: "center",
    height: 300,
    display: "flex",
    borderRadius: 2,
    padding: 10,
    justifyContent: "center",
  },
  actionContainer: {
    flexDirection: "row",
    display: "flex",
    justifyContent: "center",
  },
  fields: {
    flexDirection: "column",
    display: "flex",
    padding: 10,
    width: "80%",
    alignContent: "center",
  },
  input: {
    height: 25,
    margin: 5,
  },
});
type BookingModalProps = {
  seat: any;
  onSubmit: any;
  onCancel: any;
};

type UserData = {
  first_name: string;
  last_name: string;
  email: string;
};

export const BookingModal = (props: BookingModalProps) => {
  const [userData, setUserData] = useState<UserData>();

  const handleChange = (key: string, value: string) =>{
    const temp = userData
    temp[key] = value
    setUserData(temp)
  }
  return (
    <div className={modalClasses.container}>
      <div className={modalClasses.fields}>
        <input
          name="First Name"
          onChange={(e) => handleChange("first_name", e.target.value)}
          className={modalClasses.input}
        />
        <input
          name="Last Name"
          onChange={(e) => handleChange("last_name", e.target.value)}
          className={modalClasses.input}
        />
        <input
          name="Email"
          onChange={(e) => handleChange("email", e.target.value)}
          className={modalClasses.input}
        />
      </div>
      <div className={modalClasses.actionContainer}>
        <Button onClick={props.onSaveChanges}>Book</Button>
        <Button onClick={props.onCancel}>Cancel</Button>
      </div>
    </div>
  );
};
