import axios from "axios";

const BASE_URL = "https://bus-reservation-api.onrender.com/api";

const v1Endpoints = ["/reservations", "/buses"];

export default function Request() {}

Request.get = async (path: string, params?: any) => {
  const version = v1Endpoints.includes(path) ? "/v1" : "/v2";
  return await axios.get(`${BASE_URL}${version}${path}`, params);
};

Request.post = async (path: string, params?: any) => {
  const version = v1Endpoints.includes(path) ? "/v1" : "/v2";
  return await axios.post(`${BASE_URL}${version}${path}`, params);
};

Request.put = async (path: string, params?: any) => {
  const version = v1Endpoints.includes(path) ? "/v1" : "/v2";
  return await axios.put(`${BASE_URL}${version}${path}`, params);
};

Request.delete = async (path: string, params?: any) => {
  const version = v1Endpoints.includes(path) ? "/v1" : "/v2";
  return await axios.delete(`${BASE_URL}${version}${path}`, params);
};
